(function() {
  var coffeecup, meryl;

  meryl = require('meryl');

  coffeecup = require('../../src/coffeecup');

  meryl.h('GET /', function(req, resp) {
    var people;
    people = ['bob', 'alice', 'meryl'];
    return resp.render('layout', {
      content: 'index',
      context: {
        people: people
      }
    });
  });

  meryl.run({
    templateDir: 'templates',
    templateExt: '.coffee',
    templateFunc: coffeecup.adapters.meryl
  });

  console.log('Listening on 3000...');

}).call(this);
