(function() {
  var assert, cc;

  cc = require('../lib/coffeecup');

  assert = require('assert');

  describe('Attributes', function() {
    return describe("a href: '/', title: 'Home'", function() {
      return it('should render <a href="/" title="Home"></a>', function() {
        var a;
        a = function() {
          return a({
            href: '/',
            title: 'Home'
          });
        };
        return assert.equal(cc.render(a), '<a href="/" title="Home"></a>');
      });
    });
  });

  describe('Attribute values', function() {
    return describe("br vrai: yes, faux: no, undef: @foo, nil: null, str: 'str', num: 42, arr: [1, 2, 3], obj: {foo: 'bar'}, func: ->", function() {
      return it('should render <br vrai="vrai" str="str" num="42" arr="1,2,3" obj-foo="bar" func="(function () {}).call(this);" />', function() {
        var a;
        a = function() {
          return br({
            vrai: true,
            faux: false,
            undef: this.foo,
            nil: null,
            str: 'str',
            num: 42,
            arr: [1, 2, 3],
            obj: {
              foo: 'bar'
            },
            func: function() {}
          });
        };
        return assert.equal(cc.render(a), '<br vrai="vrai" str="str" num="42" arr="1,2,3" obj-foo="bar" func="(function () {}).call(this);" />');
      });
    });
  });

  describe('Attributes optimized', function() {
    return describe("a href: '/', title: 'Home'", function() {
      return it('should render <a href="/" title="Home"></a>', function() {
        var a;
        a = function() {
          return a({
            href: '/',
            title: 'Home'
          });
        };
        return assert.equal(cc.render(a, {
          optimize: true,
          cache: true
        }), '<a href="/" title="Home"></a>');
      });
    });
  });

  describe('Attribute values optimized', function() {
    return describe("br vrai: yes, faux: no, undef: @foo, nil: null, str: 'str', num: 42, arr: [1, 2, 3], obj: {foo: 'bar'}, func: ->", function() {
      return it('should render <br vrai="vrai" str="str" num="42" arr="1,2,3" obj-foo="bar" func="(function () {}).call(this);" />', function() {
        var a;
        a = function() {
          return br({
            vrai: true,
            faux: false,
            undef: this.foo,
            nil: null,
            str: 'str',
            num: 42,
            arr: [1, 2, 3],
            obj: {
              foo: 'bar'
            },
            func: function() {}
          });
        };
        return assert.equal(cc.render(a, {
          optimize: true
        }), '<br vrai="vrai" str="str" num="42" arr="1,2,3" obj-foo="bar" func="(function () {}).call(this);" />');
      });
    });
  });

}).call(this);
