(function() {
  var assert, cc;

  cc = require('../lib/coffeecup');

  assert = require('assert');

  describe('Comments', function() {
    return describe('comment "Comment"', function() {
      return it('should render <!--Comment-->', function() {
        var c;
        c = function() {
          return comment("Comment");
        };
        return assert.equal(cc.render(c), '<!--Comment-->');
      });
    });
  });

  describe('Comments optimized', function() {
    return describe('comment "Comment"', function() {
      return it('should render <!--Comment-->', function() {
        var c;
        c = function() {
          return comment("Comment");
        };
        return assert.equal(cc.render(c, {
          optimized: true,
          cache: true
        }), '<!--Comment-->');
      });
    });
  });

}).call(this);
