(function() {
  var assert, cc;

  cc = require('../lib/coffeecup');

  assert = require('assert');

  describe('Common tag', function() {
    return describe("p 'foo'", function() {
      return it('should render foo in <p> tag', function() {
        var p;
        p = function() {
          return p('foo');
        };
        return assert.equal(cc.render(p), '<p>foo</p>');
      });
    });
  });

  describe('Common tag optimized', function() {
    return describe("p 'foo'", function() {
      return it('should render foo in <p> tag', function() {
        var p;
        p = function() {
          return p('foo');
        };
        return assert.equal(cc.render(p, {
          optimized: true,
          cache: true
        }), '<p>foo</p>');
      });
    });
  });

}).call(this);
