(function() {
  var assert, cc;

  cc = require('../lib/coffeecup');

  assert = require('assert');

  describe('doctype', function() {
    it('default should render html5 doctype', function() {
      var expected, template;
      template = "doctype()";
      expected = '<!DOCTYPE html>';
      return assert.equal(cc.render(template), expected);
    });
    it('xml should render xml header', function() {
      var expected, template;
      template = "doctype 'xml'";
      expected = '<?xml version="1.0" encoding="utf-8" ?>';
      return assert.equal(cc.render(template), expected);
    });
    it('5 should render html 5 doctype', function() {
      var expected, template;
      template = "doctype 5";
      expected = '<!DOCTYPE html>';
      return assert.equal(cc.render(template), expected);
    });
    return it('transitional should render transitional doctype', function() {
      var expected, template;
      template = "doctype 'transitional'";
      expected = '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">';
      return assert.equal(cc.render(template), expected);
    });
  });

  describe('doctype optimized', function() {
    it('default should render html5 doctype', function() {
      var expected, template;
      template = "doctype()";
      expected = '<!DOCTYPE html>';
      return assert.equal(cc.render(template, {
        optimized: true,
        cache: true
      }), expected);
    });
    it('xml should render xml header', function() {
      var expected, template;
      template = "doctype 'xml'";
      expected = '<?xml version="1.0" encoding="utf-8" ?>';
      return assert.equal(cc.render(template, {
        optimized: true,
        cache: true
      }), expected);
    });
    it('5 should render html 5 doctype', function() {
      var expected, template;
      template = "doctype 5";
      expected = '<!DOCTYPE html>';
      return assert.equal(cc.render(template, {
        optimized: true,
        cache: true
      }), expected);
    });
    return it('transitional should render transitional doctype', function() {
      var expected, template;
      template = "doctype 'transitional'";
      expected = '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">';
      return assert.equal(cc.render(template, {
        optimized: true,
        cache: true
      }), expected);
    });
  });

}).call(this);
