(function() {
  var assert, cc;

  cc = require('../lib/coffeecup');

  assert = require('assert');

  describe('HereDocs', function() {
    return describe('script """\n   $(document).ready(function(){\n     alert(\'test\');\n   });\n """', function() {
      return it('should render <script>$(document).ready(function(){\n  alert(\'test\');\n});</script>', function() {
        var h;
        h = function() {
          return script("$(document).ready(function(){\n  alert('test');\n});");
        };
        return assert.equal(cc.render(h), '<script>$(document).ready(function(){\n  alert(\'test\');\n});</script>');
      });
    });
  });

  describe('HereDocs optimized', function() {
    return describe('script """\n   $(document).ready(function(){\n     alert(\'test\');\n   });\n """', function() {
      return it('should render <script>$(document).ready(function(){\n  alert(\'test\');\n});</script>', function() {
        var h;
        h = function() {
          return script("$(document).ready(function(){\n  alert('test');\n});");
        };
        return assert.equal(cc.render(h, {
          optimized: true,
          cache: true
        }), '<script>$(document).ready(function(){\n  alert(\'test\');\n});</script>');
      });
    });
  });

}).call(this);
