(function() {
  var assert, cc;

  cc = require('../lib/coffeecup');

  assert = require('assert');

  describe('ID/class shortcut (ID only)', function() {
    return describe("div '#myid', 'foo'", function() {
      return it('should render <div id="myid">foo</div>', function() {
        var t;
        t = function() {
          return div('#myid', 'foo');
        };
        return assert.equal(cc.render(t), '<div id="myid">foo</div>');
      });
    });
  });

  describe('ID/class shortcut (one class only)', function() {
    return describe("div '.myclass', 'foo'", function() {
      return it('should render <div class="myclass">foo</div>', function() {
        var t;
        t = function() {
          return div('.myclass', 'foo');
        };
        return assert.equal(cc.render(t), '<div class="myclass">foo</div>');
      });
    });
  });

  describe('ID/class shortcut (multiple classes)', function() {
    return describe("div '.myclass.myclass2.myclass3', 'foo'", function() {
      return it('should render <div class="myclass myclass2 myclass3">foo</div>', function() {
        var t;
        t = function() {
          return div('.myclass.myclass2.myclass3', 'foo');
        };
        return assert.equal(cc.render(t), '<div class="myclass myclass2 myclass3">foo</div>');
      });
    });
  });

  describe('ID/class shortcut (no string contents)', function() {
    return describe("img '#myid.myclass', src: '/pic.png'", function() {
      return it('should render <img id="myid" class="myclass" src="/pic.png" />', function() {
        var t;
        t = function() {
          return img('#myid.myclass', {
            src: '/pic.png'
          });
        };
        return assert.equal(cc.render(t), '<img id="myid" class="myclass" src="/pic.png" />');
      });
    });
  });

  describe('ID/class shortcut (ID only) optimized', function() {
    return describe("div '#myid', 'foo'", function() {
      return it('should render <div id="myid">foo</div>', function() {
        var t;
        t = function() {
          return div('#myid', 'foo');
        };
        return assert.equal(cc.render(t, {
          optimized: true,
          cache: true
        }), '<div id="myid">foo</div>');
      });
    });
  });

  describe('ID/class shortcut (one class only) optimized', function() {
    return describe("div '.myclass', 'foo'", function() {
      return it('should render <div class="myclass">foo</div>', function() {
        var t;
        t = function() {
          return div('.myclass', 'foo');
        };
        return assert.equal(cc.render(t, {
          optimized: true,
          cache: true
        }), '<div class="myclass">foo</div>');
      });
    });
  });

  describe('ID/class shortcut (multiple classes) optimized', function() {
    return describe("div '.myclass.myclass2.myclass3', 'foo'", function() {
      return it('should render <div class="myclass myclass2 myclass3">foo</div>', function() {
        var t;
        t = function() {
          return div('.myclass.myclass2.myclass3', 'foo');
        };
        return assert.equal(cc.render(t, {
          optimized: true,
          cache: true
        }), '<div class="myclass myclass2 myclass3">foo</div>');
      });
    });
  });

  describe('ID/class shortcut (no string contents) optimized', function() {
    return describe("img '#myid.myclass', src: '/pic.png'", function() {
      return it('should render <img id="myid" class="myclass" src="/pic.png" />', function() {
        var t;
        t = function() {
          return img('#myid.myclass', {
            src: '/pic.png'
          });
        };
        return assert.equal(cc.render(t, {
          optimized: true,
          cache: true
        }), '<img id="myid" class="myclass" src="/pic.png" />');
      });
    });
  });

}).call(this);
