(function() {
  var assert, cc;

  cc = require('../lib/coffeecup');

  assert = require('assert');

  describe('IE conditionals', function() {
    return describe(" ie 'gte IE8', -> link href: 'ie.css', rel: 'stylesheet'", function() {
      return it('should render <!--[if gte IE8]><link href="ie.css" rel="stylesheet" /><![endif]-->', function() {
        var h;
        h = function() {
          return ie('gte IE8', function() {
            return link({
              href: 'ie.css',
              rel: 'stylesheet'
            });
          });
        };
        return assert.equal(cc.render(h), '<!--[if gte IE8]><link href="ie.css" rel="stylesheet" /><![endif]-->');
      });
    });
  });

  describe('IE conditionals optimized', function() {
    return describe(" ie 'gte IE8', -> link href: 'ie.css', rel: 'stylesheet'", function() {
      return it('should render <!--[if gte IE8]><link href="ie.css" rel="stylesheet" /><![endif]-->', function() {
        var h;
        h = function() {
          return ie('gte IE8', function() {
            return link({
              href: 'ie.css',
              rel: 'stylesheet'
            });
          });
        };
        return assert.equal(cc.render(h, {
          optimized: true,
          cache: true
        }), '<!--[if gte IE8]><link href="ie.css" rel="stylesheet" /><![endif]-->');
      });
    });
  });

}).call(this);
