(function() {
  var assert, cc;

  cc = require('../lib/coffeecup');

  assert = require('assert');

  describe('Inline', function() {
    return describe('#cede(name, attr)', function() {
      return it('should render', function() {
        var t;
        t = function() {
          return p("This text could use " + (cede(function() {
            return strong(function() {
              return a({
                href: '/'
              }, 'a link');
            });
          })) + ".");
        };
        return assert.equal(cc.render(t), '<p>This text could use <strong><a href="/">a link</a></strong>.</p>');
      });
    });
  });

  describe('Inline optimized', function() {
    return describe('#cede(name, attr)', function() {
      return it('should render', function() {
        var t;
        t = function() {
          return p("This text could use " + (cede(function() {
            return strong(function() {
              return a({
                href: '/'
              }, 'a link');
            });
          })) + ".");
        };
        return assert.equal(cc.render(t, {
          optimized: true,
          cache: true
        }), '<p>This text could use <strong><a href="/">a link</a></strong>.</p>');
      });
    });
  });

}).call(this);
