(function() {
  var assert, cc;

  cc = require('../lib/coffeecup');

  assert = require('assert');

  describe('literal', function() {
    return describe('#text(value)', function() {
      return it('should render just plain text', function() {
        var t;
        t = function() {
          return text('foobar');
        };
        return assert.equal(cc.render(t), 'foobar');
      });
    });
  });

  describe('literal optimized', function() {
    return describe('#text(value)', function() {
      return it('should render just plain text', function() {
        var t;
        t = function() {
          return text('foobar');
        };
        return assert.equal(cc.render(t, {
          optimized: true,
          cache: true
        }), 'foobar');
      });
    });
  });

}).call(this);
