(function() {
  var assert, cc;

  cc = require('../lib/coffeecup');

  assert = require('assert');

  describe('Self Closing Tags', function() {
    describe('#img(name, attr)', function() {
      it('should render', function() {
        var t;
        t = function() {
          return img();
        };
        return assert.equal(cc.render(t), '<img />');
      });
      return it('should render with attributes', function() {
        var t;
        t = function() {
          return img({
            src: 'http://foo.jpg.to'
          });
        };
        return assert.equal(cc.render(t), '<img src="http://foo.jpg.to" />');
      });
    });
    describe('#br()', function() {
      return it('should render', function() {
        var t;
        t = function() {
          return br();
        };
        return assert.equal(cc.render(t), '<br />');
      });
    });
    return describe('#link()', function() {
      return it('should render with attributes', function() {
        var t;
        t = function() {
          return link({
            href: '/foo.css',
            rel: 'stylesheet'
          });
        };
        return assert.equal(cc.render(t), '<link href="/foo.css" rel="stylesheet" />');
      });
    });
  });

  describe('Self Closing Tags optimized', function() {
    describe('#img(name, attr)', function() {
      it('should render', function() {
        var t;
        t = function() {
          return img();
        };
        return assert.equal(cc.render(t, {
          optimized: true,
          cache: true
        }), '<img />');
      });
      return it('should render with attributes', function() {
        var t;
        t = function() {
          return img({
            src: 'http://foo.jpg.to'
          });
        };
        return assert.equal(cc.render(t), '<img src="http://foo.jpg.to" />');
      });
    });
    describe('#br()', function() {
      return it('should render', function() {
        var t;
        t = function() {
          return br();
        };
        return assert.equal(cc.render(t, {
          optimized: true,
          cache: true
        }), '<br />');
      });
    });
    return describe('#link()', function() {
      return it('should render with attributes', function() {
        var t;
        t = function() {
          return link({
            href: '/foo.css',
            rel: 'stylesheet'
          });
        };
        return assert.equal(cc.render(t, {
          optimized: true,
          cache: true
        }), '<link href="/foo.css" rel="stylesheet" />');
      });
    });
  });

}).call(this);
