(function() {
  var assert, cc;

  cc = require('../lib/coffeecup');

  assert = require('assert');

  describe('Stylus', function() {
    return describe('stylus  "html, body\\n  margin: 0"', function() {
      return it('should render <style>html,body{margin:0}\\n</style>', function() {
        var s;
        s = function() {
          return stylus("html, body\n  margin: 0");
        };
        return assert.equal(cc.render(s), "<style>html,body{margin:0}\n</style>");
      });
    });
  });

  describe('Stylus optimized', function() {
    return describe('stylus  "html, body\\n  margin: 0"', function() {
      return it('should render <style>html,body{margin:0}\\n</style>', function() {
        var s;
        s = function() {
          return stylus("html, body\n  margin: 0");
        };
        return assert.equal(cc.render(s, {
          optimized: true,
          cache: true
        }), "<style>html,body{margin:0}\n</style>");
      });
    });
  });

}).call(this);
